mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

mapper.select:
SELECT A.*, M.nickname AS extra__writer FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id WHERE 1
AND boardId = #{boardId}
AND (
A.title LIKE CONCAT('%', #{searchKeyword}, '%')
A.`body` LIKE CONCAT('%', #{searchKeyword}, '%')
M.nickname LIKE CONCAT('%', #{searchKeyword}, '%')
A.title LIKE CONCAT('%', #{searchKeyword}, '%')
)
ORDER BY A.id DESC
LIMIT #{limitFrom}, #{limitTake}
